export default (args) => {
    const { renderings } = args;
    const placeholderNames = renderings.reduce((result, rendering) => {
        if (rendering.exposedPlaceholders && Array.isArray(rendering.exposedPlaceholders)) {
            return [...result, ...rendering.exposedPlaceholders];
        }
        return result;
    }, []);
    return Object.assign(Object.assign({}, args), { placeholderNames: [...args.placeholderNames, ...placeholderNames] });
};
